CREATE DATABASE QLBanMayTinh
GO

USE QLBanMayTinh
GO

create table ProductCategory
(
	ID int identity(1,1) primary key not null,
	Name nvarchar(250),
	MetaTitle varchar(250),
	Icon varchar(100),
	Status bit,
	CreatedDate datetime,
)

create table Producer
(
	ID int identity(1,1) primary key not null,
	Name nvarchar(250),
	MetaTitle varchar(250),
	Status bit,
	CreatedDate datetime,
)

create table Product
(
	ID int identity(1,1) primary key not null,
	Name nvarchar(250),
	MetaTitle varchar(250),
	Images nvarchar(250),
	MoreImages xml,
	Price decimal(18,0),
	Quantity int,
	Descriptions ntext,
	Detail ntext,
	TopHot date,
	CreatedDate datetime,
	CreatedBy varchar(50),
	ModifiedDate datetime,
	ModifiedBy varchar(50),
	Status bit,
	CategoryID int foreign key(CategoryID) references ProductCategory(ID) not null,
	ProducerID int foreign key(ProducerID) references Producer(ID) not null,
)
create table GroupUser
(
	ID int identity(1,1) primary key not null,
	Name nvarchar(50),
	Descriptions nvarchar(50),
)

create table Users
(
	ID int identity(1,1) primary key not null,
	Username nvarchar(50),
	Password varchar(52),
	Name nvarchar(50),
	Gender bit default 1,
	BirthDay date,
	Phone char(10),
	Email varchar(250),
	UserAddress nvarchar(500),
	CreatedDate datetime,
	CreatedBy varchar(50),
	ModifiedDate datetime,
	ModifiedBy varchar(50),
	Status bit,
	GroupUserID int foreign key(GroupUserID) references GroupUser(ID) not null,
)

create table Orders
(
	ID int identity(1,1) primary key not null,
	UserID int foreign key(UserID) references Users(ID),
	ShipCreatedDate datetime,
	ShipName nvarchar(50),
	ShipPhone char(10),
	ShipAddress nvarchar(500),
	ShipEmail varchar(250),
	Status bit
)

create table OrderDetail
(
	OrderID int foreign key(OrderID) references Orders(ID) not null,
	ProductID int foreign key(ProductID) references Product(ID) not null,
	Quantity int,
	Price decimal(18, 0),
	Status bit
)

create table ContentCategory
(
	ID int identity(1,1) primary key not null,
	Name nvarchar(50),
	MetaTitle varchar(250),
	CreatedDate datetime,
	Status bit,
)

create table Content
(
	ID int identity(1,1) primary key not null,
	Name nvarchar(50),
	MetaTitle varchar(250),
	Descriptions nvarchar(500),
	Images nvarchar(250),
	Detail ntext,
	CreatedDate datetime,
	CreatedBy varchar(50),
	ModifiedDate datetime,
	ModifiedBy varchar(50),
	Status bit,
	TopHot date,
	ViewCount int,
	ContentCategoryID int foreign key(ContentCategoryID) references ContentCategory(ID) not null,
)

